import React from "react";
import styled, { css } from "styled-components";

const Logo = styled.div`
  font-size: 1.5rem;
`;
const Bar = styled.div`
  display: grid;
  grid-template-columns: 180px auto 100px 100px;
  padding: 10px;
  background: #1f1b24;
  color: white;
`;

const ControllButtonElem = styled.div`
  cursor: pointer;
  ${(props) =>
    props.active &&
    css`
      color: #bb86fc;
    `}
`;

function controllButton({ name }) {
  return <ControllButtonElem>{name}</ControllButtonElem>;
}
const NavBar = () => {
  return (
    <Bar>
      <Logo>Cryptoviz</Logo>
      <div></div>
      <ControllButtonElem name="News" />
      <ControllButtonElem name="settings" />
    </Bar>
  );
};

export default NavBar;
