import React from "react";
import styled from "styled-components";
import CoinGrid from "./CoinGrid";
import cryptoApi from "../api/crypto";

const Board = styled.div`
  height: auto;
`;

class DashBoard extends React.Component {
  state = { coinsList: [] };

  fetchCoins() {
    cryptoApi
      .get("/data/all/coinlist")
      .then((response) => {
        // this response is object need to convert it to the array or need to find it better way
        return response.data.Data;
      })
      .catch((error) => {
        console.log(error);
      });
  }

  componentDidMount() {
    const response = this.fetchCoins();
    this.setState({ coinsList: Object.values(response) });
    console.log(this.state.coinsList[0]);
  }

  render() {
    return (
      <Board>
        <CoinGrid coins={this.state.coinsList} />
      </Board>
    );
  }
}

export default DashBoard;
