import React from "react";
import styled from "styled-components";
import CoinGrid from "./CoinGrid";
import cryptoApi from "../api/crypto";

const Board = styled.div`
  height: 100vh;
  color: #fff;
`;

class DashBoard extends React.Component {
  state = { coinsList: [] };

  componentDidMount() {
    cryptoApi
      .get("/data/all/coinlist")
      .then((response) => {
        // response.data.Data
        this.setState({ coinsList: response.data.Data });
      })
      .catch((error) => {
        console.log(error);
      });
  }

  render() {
    if (this.state.coinsList.length === 0) {
      return null;
    }
    // return <Board>{this.state.coinsList.length}</Board>;
    return this.state.coinsList.length;
  }
}

export default DashBoard;
