import React from "react";
import styled from "styled-components";

const Grid = styled.div`
  display: grid;
  grid-template-columns: repeat(4, 1fr);
  grid-gap: 15px;
  color: #fff;
  margin-top: 20px;
`;

const Tile = styled.div`
  background: #1f1b24;
  color: #fff;
  padding: 15px;
`;

const Image = styled.img`
  height: 100px;
  width: 100px;
`;

const ControlImage = (src, alt) => {
  return <Image src={src} alt={alt} />;
};

class CoinGrid extends React.Component {
  renderCoinsList() {
    const BASE_URL = "https://www.cryptocompare.com";
    return this.props.coins.map((coin, index) => {
      return (
        <Tile key={index}>
          <ControlImage
            src={BASE_URL + coin.ImageUrl}
            alt={coin.Name}
          ></ControlImage>
          <p>{coin.CoinName}</p>
        </Tile>
      );
    });
  }
  render() {
    return <Grid>{this.renderCoinsList()}</Grid>;
  }
}

export default CoinGrid;
